var result = {};
result["count"] = keep.view.getDocumentCount();
var counter = 0;
print("COUNT = " + keep.view.getDocumentCount());
var docs = [];
keep.view.forEachDocument(0, 2, function(doc, loop) {
    print("loop: " + doc.getItemValue("name"));
    counter++;
    docs.push({
        name: doc.getItemValue("name"),
        email: doc.getItemValue("email")
    })
    result.docs = docs
})
keep.results.add(result)
print("RESULT VIEW SIZE=" + keep.results.size());
print("COUNTER=" + counter);

